import{_ as i,c as l,o as e,d as t}from"./app.c282a12f.js";const S=JSON.parse('{"title":"CSS","description":"","frontmatter":{},"headers":[{"level":2,"title":"01-继承","slug":"_01-继承","link":"#_01-继承","children":[]},{"level":2,"title":"02-css预处理工具","slug":"_02-css预处理工具","link":"#_02-css预处理工具","children":[]},{"level":2,"title":"03-盒子模型","slug":"_03-盒子模型","link":"#_03-盒子模型","children":[]},{"level":2,"title":"04-BFC","slug":"_04-bfc","link":"#_04-bfc","children":[]}],"relativePath":"Web/interview/前端基础/CSS.md"}'),s={name:"Web/interview/前端基础/CSS.md"},a=t('<h1 id="css" tabindex="-1">CSS <a class="header-anchor" href="#css" aria-hidden="true">#</a></h1><h2 id="_01-继承" tabindex="-1">01-继承 <a class="header-anchor" href="#_01-继承" aria-hidden="true">#</a></h2><ol><li>有继承性的属性 <ul><li>字体系列属性：font</li><li>文本系列属性：text</li><li>元素可见性：visibility</li><li>表格布局属性：border-spacing、boder-collapse</li><li>列表布局属性：list-style</li><li>光标属性：cursor</li><li>生成内容属性：quotes</li><li>页面样式属性</li><li>声音样式属性</li></ul></li><li>无继承性的属性 <ol><li>元素可见性：display</li><li>文本属性：text-shadow,white-space,vertical-align</li><li>盒子模型属性</li><li>背景属性</li><li>定位属性</li></ol></li><li>所有元素可以继承的属性：cursor，visibility</li><li>内联元素可以继承的属性：字体系列，文本系列(除text-indent,text-align)</li><li>块级元素可以继承的属性：text-indent，text-align</li></ol><h2 id="_02-css预处理工具" tabindex="-1">02-css预处理工具 <a class="header-anchor" href="#_02-css预处理工具" aria-hidden="true">#</a></h2><ol><li>概念 <ul><li>css预处理器是一个能让开发者通过预处理器自己独有的语法来生成css的程序</li><li>css预处理器种类繁多，三种主流预处理器：Sass、Less、Stylus</li></ul></li><li>比较 <ul><li>功能相同，语法上略有差异</li></ul></li></ol><h2 id="_03-盒子模型" tabindex="-1">03-盒子模型 <a class="header-anchor" href="#_03-盒子模型" aria-hidden="true">#</a></h2><ol><li>概念： <ul><li>css盒子模型本质上是一个盒子，封装周围的HTML元素，有content,padding,boder,margin四个属性</li><li>分类：标准盒子模型与IE盒子模型</li></ul></li><li>标准盒子模型 <ul><li>content+border+padding+margin</li><li>box-sizing:content-box</li></ul></li><li>怪异盒子模型 <ul><li>content+margin</li><li>box-sizing:border-box</li></ul></li></ol><h2 id="_04-bfc" tabindex="-1">04-BFC <a class="header-anchor" href="#_04-bfc" aria-hidden="true">#</a></h2><ol><li>BFC <ol><li>概念：BFC是CSS布局的一个概念，是一块独立的渲染区域，里面的元素不会影响到外部的元素</li><li>什么情况下创建出BFC <ul><li>根元素</li><li>浮动</li><li>绝对定位</li><li>行内块元素</li><li>表格单元</li><li>弹性盒</li></ul></li></ol></li></ol>',9),n=[a];function r(c,o,d,_,h,u){return e(),l("div",null,n)}const x=i(s,[["render",r]]);export{S as __pageData,x as default};
