import{_ as l,c as i,o as e,d as a}from"./app.c282a12f.js";const m=JSON.parse('{"title":"HTML","description":"","frontmatter":{},"headers":[{"level":2,"title":"01-html标签的类型及作用","slug":"_01-html标签的类型及作用","link":"#_01-html标签的类型及作用","children":[]},{"level":2,"title":"02-H5新特性","slug":"_02-h5新特性","link":"#_02-h5新特性","children":[]},{"level":2,"title":"03-语义化标签的优点","slug":"_03-语义化标签的优点","link":"#_03-语义化标签的优点","children":[]},{"level":2,"title":"04-audio标签的API","slug":"_04-audio标签的api","link":"#_04-audio标签的api","children":[]}],"relativePath":"Web/interview/前端基础/HTML.md"}'),t={name:"Web/interview/前端基础/HTML.md"},r=a('<h1 id="html" tabindex="-1">HTML <a class="header-anchor" href="#html" aria-hidden="true">#</a></h1><h2 id="_01-html标签的类型及作用" tabindex="-1">01-html标签的类型及作用 <a class="header-anchor" href="#_01-html标签的类型及作用" aria-hidden="true">#</a></h2><ol><li>DOCTYPE <ul><li>它是指示web浏览器关于页面使用哪个HTML版本进行编写的指令</li></ul></li><li>head <ul><li>它是所有头部元素的容器，绝大多数头部标签的内容不会显示给读者</li></ul></li><li>body <ul><li>用于定义文档的主体，包含了文档的所有内容</li><li>该标签支持html的全局属性和事件属性</li></ul></li></ol><h2 id="_02-h5新特性" tabindex="-1">02-H5新特性 <a class="header-anchor" href="#_02-h5新特性" aria-hidden="true">#</a></h2><ol><li>语义化标签：header,footer,nav,article,section</li><li>表单控件：calendar,data,time,email,url,search</li><li>媒体播放：video和audio</li><li>本地存储：localStorage和sessionStorage</li></ol><h2 id="_03-语义化标签的优点" tabindex="-1">03-语义化标签的优点 <a class="header-anchor" href="#_03-语义化标签的优点" aria-hidden="true">#</a></h2><ol><li>易于用户阅读，样式丢失的时候能让页面呈现清晰的结构</li><li>有利于SEO，搜索引擎根据标签来确定上下文和各个关键字的权重</li><li>方便屏幕阅读器解析，如盲人阅读器根据语义渲染网页</li><li>有利于开发和维护，语义化更具可读性，代码更好维护</li></ol><h2 id="_04-audio标签的api" tabindex="-1">04-audio标签的API <a class="header-anchor" href="#_04-audio标签的api" aria-hidden="true">#</a></h2><ol><li>常用属性 <ul><li>src：播放的音乐url地址</li><li>preload：预加载（在页面被加载时进行加载或者说缓冲音频），使用autoplay，则该属性失效</li><li>loop：循环播放</li><li>controls：是否显示默认控制条（控制按钮）</li><li>autoplay：自动播放</li></ul></li><li>常用函数 <ul><li>load()：加载音视频</li><li>play()：加载并播放音视频</li><li>pause()：暂停处于播放状态的音频</li></ul></li></ol>',9),o=[r];function d(h,n,s,_,c,u){return e(),i("div",null,o)}const f=l(t,[["render",d]]);export{m as __pageData,f as default};
